// Get email text
const text = $json.text || $input.first().json.test_email;

// Extract "Chat:" section only
const chatSection = text.split("Chat:")[1] || "";

// Parse questions
const questions = [];
const regex = /\d+:\s.*?\):\s([\s\S]*?)(?=\n\d+:|$)/g;
let match;

while ((match = regex.exec(chatSection)) !== null) {
  let q = match[1].trim();

  // Normalize whitespace: collapse \n and multiple spaces
  q = q.replace(/\s+/g, " ");

  if (q) {
    questions.push({ question: q });
  }
}

// Extract attendee email address from the "E-Mail:" line
let replyTo = null;
const emailMatch = text.match(/E-Mail:\s*([^\s<]+@[^\s<]+)/i);
if (emailMatch) {
  replyTo = emailMatch[1].trim();
  // Normalize: lowercase + strip trailing punctuation
  replyTo = replyTo.replace(/[.,;]+$/, "").toLowerCase();
}

return {
  threadId: $input.first().json.threadId || 'none',
  replyTo: replyTo || 'not_found@example.com',
  questions: questions.map(q => ({ json: q }))
};

// Get all incoming items
const data = $input.all();

// Intro blurb
const intro = `<p>Vielen Dank für Ihre Teilnahme am Webinar. Im Folgenden finden Sie die Antworten auf die gestellten Fragen:</p>`;

// Map over each item and format Question + Answer
const qaPairs = data.map((item,i) => {
  const question = $('Parse Questions').first().json.questions[i]?.json?.question || "";
  const answer   = item.json.message?.content || "";
  return `<p><strong>${question}</strong><br>${answer}</p>`;
});

// Join all Q&A pairs
const body = qaPairs.join("\n\n");

// Sign-off
const outro = `<p>Mit freundlichen Grüßen,<br>Ihr Expertenteam</p>`;

// Put it all together
const combinedHtml = `${intro}\n\n${body}\n\n${outro}`;

// Subject line
const subject = "Ihre Fragen aus dem Webinar – unsere Antworten";

return [{
  json: {
    to: $('Parse Questions').first().json.replyTo,
    subject,
    emailBody: combinedHtml
  }
}];

