={
  "model": "sonar-pro",
  "messages": [
    {
      "role": "system",
      "content": "Act as an expert news researcher with access to trusted, high-authority sources. Your role is to gather comprehensive, accurate, and up-to-date summaries on a specific news topic. Prioritize credibility, clarity, and depth in your findings, ensuring all key developments are covered."
    },
    {
      "role": "user",
      "content": "{{ $json.message.content.split(/\n+/).join(' ').replaceAll('"', ' ') }}"
    }
  ]
}
