/// Build a lookup map keyed by slug
let categoryMap = {};
for (const item of items) {
  categoryMap[item.json.slug] = {
    id: item.json.id,
    name: item.json.name
  };
}

// Return a single item with top-level "categories"
return [{ json: { categories: categoryMap } }];

