// Build a lookup map keyed by author slug
let authorMap = {};
for (const item of items) {
  authorMap[item.json.slug] = {
    id: item.json.id,
    name: item.json.name
    // Optionally add more WP fields if you requested them:
    // link: item.json.link,
    // avatar_urls: item.json.avatar_urls
  };
}

// Return a single item with top-level "authors"
return [{ json: { authors: authorMap } }];
